"use strict";

const SysQosConfigurationAbl = require("../../abl/sys-qos-configuration-abl.js");

class SysQos {
  /**
   *
   * @param {string} awid
   * @param {Object} dtoIn attributes same as sys/uuSubAppInstance/qos/setConfiguration uuCmd
   * @returns {Promise<Object>} uuObject sysQosConfiguration
   */
  async setConfiguration(awid, dtoIn) {
    return await SysQosConfigurationAbl.setConfiguration(awid, dtoIn);
  }

  /**
   *
   * @param {string} awid
   * @returns {Promise<{Object}>} uuObject sysQosConfiguration
   */
  async getConfiguration(awid) {
    return await SysQosConfigurationAbl.getConfiguration(awid)
  }
}

module.exports = new SysQos();