"use strict";
const SysQosConfigurationAbl = require("../../abl/sys-qos-configuration-abl");

class SysQosConfigurationController {
  setConfiguration(ucEnv) {
    return SysQosConfigurationAbl.setConfiguration(ucEnv.getUri().getAwid(), ucEnv.getDtoIn());
  }

  getConfiguration(ucEnv) {
    return SysQosConfigurationAbl.getConfiguration(ucEnv.getUri().getAwid());
  }
}

module.exports = new SysQosConfigurationController();
