const SysQosError = require("./sys-qos-error");

const SetConfiguration = {
  UC_CODE: `${SysQosError.ERROR_PREFIX}sys/uuSubAppInstance/qos/setConfiguration/`,

  InvalidDtoInError: class extends SysQosError {
    constructor() {
      super(...arguments);
      this.status = 400;
      this.message = "DtoIn is not valid.";
      this.code = `${SetConfiguration.UC_CODE}invalidDtoIn`;
    }
  },
  CreateQuotaFailed: class extends SysQosError {
    constructor() {
      super(...arguments);
      this.status = 400;
      this.message = "Creation of sysQosQuota uuObject has failed.";
      this.code = `${SetConfiguration.UC_CODE}createSysQosQuotaFailed`;
    }
  },
};

module.exports = { SetConfiguration };
