const { BaseError } = require("uu_appg01_core-utils");
const SysQosError = require("./sys-qos-error");

const ConfigurationError = {
  UC_CODE: `${SysQosError.ERROR_PREFIX}`,

  InvalidDeployConfigurationParamsError: class extends BaseError {
    constructor() {
      super(...arguments);
      this.message = "Qos configuration parameters are not valid.";
      this._code = `${ConfigurationError.UC_CODE}invalidQosConfigurationParameters`;
    }
  }
};

module.exports = { ConfigurationError };
