const { UseCaseError } = require("uu_appg01_core-appserver");

class SysQosError extends UseCaseError {
  static get ERROR_PREFIX() {
    return "uu-app-qos/";
  }

  constructor(code, message, paramMap = {}, status, cause = null) {
    if (paramMap instanceof Error) {
      cause = paramMap;
      paramMap = {};
    }
    super({ code, message, paramMap, status, cause });
  }
}

module.exports = SysQosError;
