const SysQosError = require("./sys-qos-error");

const RateError = {
  UC_CODE: `${SysQosError.ERROR_PREFIX}`,

  RateLimitReachedError: class extends SysQosError {
    constructor() {
      super(...arguments);
      this.status = 429;
      this.type = "error";
      this.headers["Retry-After"] = this.paramMap.retryAfter;
    }
  },
};

module.exports = { RateError };
