"use strict";
const { LoggerFactory } = require("uu_appg01_core-logging");
const LOGGER = LoggerFactory.get("uuapp.qos.RateLimiting");

/**
 * Component provides indication logging to uuAppServer Logger.
 */
class QosIndicationHandler {
  static debug(message) {
    LOGGER.debug(message);
  }

  static warn(uuAppErrorMap, code, message, paramMap) {
    LOGGER.debug(message);
    uuAppErrorMap[code] = {
      type: "warning",
      message: message,
      paramMap,
    };
  }

  static error(uuAppErrorMap, code, message, paramMap) {
    LOGGER.debug(message);
    uuAppErrorMap[code] = {
      type: "error",
      message: message,
      paramMap,
    };
  }
}

module.exports = QosIndicationHandler;
