"use strict";
const { UuObjectDao } = require("uu_appg01_objectstore");
class SysQosConfigMongo extends UuObjectDao {
  async createSchema() {
    await super.createIndex({ awid: 1 }, { unique: true });
  }

  async create(uuObject) {
    return await super.insertOne(uuObject);
  }

  async getByAwid(awid) {
    return await super.findOne({ awid });
  }

  async updateByAwid(uuObject) {
    return await super.findOneAndUpdate(
      {
        awid: uuObject.awid,
      },
      uuObject,
      "NONE"
    );
  }
}

module.exports = SysQosConfigMongo;
