const GetPrivilegedTFileAbl = require("../../abl/t-file/privileged-get-t-file-attributes-abl");
const GetTFileAttributesAbl = require("../../abl/t-file/get-t-file-attributes-abl");
const RecalculateTFilesAbl = require("../../abl/t-file/recalculate-t-file-abl");
const PrivilegedRecalculateTFilesAbl = require("../../abl/t-file/privileged-recalculate-t-file-abl");

class FilesController {
  async privilegedGetTFileAttributes(ucEnv) {
    return await GetPrivilegedTFileAbl.privilegedGetTFileAttributes(ucEnv.getDtoIn(), ucEnv.authzContext);
  }
  async getTFileAttributes(ucEnv) {
    return await GetTFileAttributesAbl.getTFileAttributes(ucEnv.getDtoIn(), ucEnv.authzContext);
  }

  async recalculateTFiles(ucEnv) {
    return await RecalculateTFilesAbl.recalculateTFiles(ucEnv.getDtoIn(), ucEnv.authzContext);
  }
  async privilegedRecalculateTFiles(ucEnv) {
    return await PrivilegedRecalculateTFilesAbl.privilegedRecalculateTFiles(ucEnv.getDtoIn(), ucEnv.authzContext);
  }
}

module.exports = new FilesController();
