"use strict";

const ResponseMap = require("./response-map");

/**
 * @typedef {import("./response-counter.js").ResponseCounterData} ResponseCounterData
 */

/**
 * @class
 * @classdesc A class that represents a map of response maps.
 */

class NestedResponseMap extends ResponseMap {
  /**
   * Creates a new response map for a given key.
   * @param {string} key reponse map identifier
   * @returns {ResponseMap}
   */
  create(key) {
    this._responseCounters[key] = new ResponseMap();
    return this._responseCounters[key];
  }
}

module.exports = NestedResponseMap;
